import React from "react";
import { Avatar, Heading, VStack, HStack } from "@chakra-ui/react";
import FullScreenSection from "./FullScreenSection";

const startering = "Little Lemon";
const Subheading = "CHICAGO";
const bio = "We are a family owned Mediterranean restaurant focused on traditional recipes served with a modern twist";

// Implement the UI for the LandingSection component according to the instructions.
// Use a combination of Avatar, Heading and VStack components.
const LandingSection = () => (
  <FullScreenSection
    justifyContent="center"
    alignItems="center"
    backgroundColor="#495e57"
  >
  <HStack>
    <VStack spacing={4}>
      <img src="./images/restauranfood.jpg" />
      <Heading as="h1" size="xl" textAlign="center" color="white">
        {startering}
      </Heading>
      <Heading as="h1" size="xl" textAlign="center" color="white">
        {Subheading}
      </Heading>
      <Heading as="h1" size="md" textAlign="center" color="white">
        {bio}
      </Heading>
    </VStack>

  </HStack>
  </FullScreenSection>
);

export default LandingSection;
