import React from "react";
import { Avatar, Heading, VStack } from "@chakra-ui/react";
import FullScreenSection from "./FullScreenSection";

const greeting = "Hello, I am Ahmad!";
const bio1 = "A frontend developer";
const bio2 = "specialized in React";

// Implement the UI for the LandingSection component according to the instructions.
// Use a combination of Avatar, Heading and VStack components.
const LandingSection = () => (
  <FullScreenSection
    justifyContent="center"
    alignItems="center"
    isDarkBackground
    backgroundColor="#2A4365"
  >
    <VStack spacing={4}>
      <Avatar name="Ahmad" size="xl" src="/path/to/avatar-image.jpg" />
      <Heading as="h1" size="md" textAlign="center" color="white">
        {greeting}
      </Heading>
      <Heading as="h1" size="xl" textAlign="center" color="white">
        {bio1}
      </Heading>
      <Heading as="h2" size="md" textAlign="center" color="white">
        {bio2}
      </Heading>
    </VStack>
  </FullScreenSection>
);

export default LandingSection;
